const textRaw=document.getElementById("text-raw"),textBruhified=document.getElementById("text-bruhified"),fancyBruh=document.getElementById("fancy-bruh"),copy=document.getElementById("copy");let bruhified;function bruhify(){bruhified=textRaw.value.replace(new RegExp(/b|p/,"gi"),"🅱️"),fancyBruh.checked&&(bruhified=bruhified.replace(new RegExp("🅱️ruh","gi"),"🅱️𝓻𝓾𝓱")),textBruhified.value=bruhified}textRaw.addEventListener("keyup",function(){bruhify()}),textRaw.addEventListener("change",function(){bruhify()}),fancyBruh.addEventListener("change",function(){bruhify()}),copy.addEventListener("click",function(){textBruhified.select(),document.execCommand("copy")});